import React, {useEffect, useState} from 'react';
import axios from "axios";

    const getTitle = (data) => {
        if (data.title !== undefined) {
            return data.title
        } else if (data.originalTitle !== undefined) {
            return data.originalTitle
        } else if (data.originalName !== undefined) {
            return data.originalName
        }
    }
const Card = () => {
    const api_key = 'api_key=46077ce24d4f82dbcaa2828a33a34916'
    const base_url = `https://image.tmdb.org/t/p/w500`
    const [data, setData] = useState([])


    useEffect(() => {
        axios.get(`https://api.themoviedb.org/3/trending/all/week?${api_key}`)
            .then(response => setData(response.data.results))

    }, [])

    return (
        data.map(data => (
            <div className='card'>
                <h2>{getTitle(data)}</h2>
                <p className='releaseDate'>{data.release_date}</p>
                <p className='overview'>{data.overview}</p>
                <p className='rate'>{data.vote_average}</p>
                <img className='cardPoster' src={base_url + data.poster_path} alt="jacket" />
            </div>
        ))
    );
};

export default Card;
