import React, {useEffect, useState} from 'react';
import axios from "axios";
import {Card, CardActionArea, CardContent, CardMedia, Typography} from "@mui/material";

const CardComponent = () => {
    const api_key = 'api_key=46077ce24d4f82dbcaa2828a33a34916'
    const base_url = `https://image.tmdb.org/t/p/w500`
    const [trendingMovies, setTrendingMovies] = useState([])

    const getTitle = (data) => {
        if (data.title !== undefined) {
            return data.title
        } else if (data.original_title !== undefined) {
            return data.original_title
        } else if (data.original_name !== undefined) {
            return data.original_name
        }
    }

    useEffect(() => {
        const fetchTrendingMovie = async () => {
                const response = await axios.get(`https://api.themoviedb.org/3/trending/all/week?${api_key}`)
                setTrendingMovies(response.data.results)
        }

        fetchTrendingMovie()
    }, [])

    return (
        trendingMovies.map((movie, index) => (
            // <div key={index} className='card'>
            //     <h2>{getTitle(movie)}</h2>
            //     <p className='releaseDate'>{movie.release_date}</p>
            //     <p className='overview'>{movie.overview}</p>
            //     <p className='rate'>{movie.vote_average}</p>
            //     <img className='cardPoster' src={base_url + movie.poster_path} alt="jacket" />
            // </div>
            <Card sx={{ maxWidth: 345 }}>
                <CardActionArea>
                    <CardMedia
                        component="img"
                        height="140"
                        image={ base_url + movie.poster_path }
                        alt="jacket"
                    />
                    <CardContent>
                        <Typography
                            gutterBottom variant="h5" 
                            component="div"
                        />
                        { getTitle(movie) }
                    </CardContent>
                </CardActionArea>
            </Card>
        ))
    );
};

export default CardComponent;
