import React, {useEffect, useState} from 'react';
import axios from "axios";

const Card = () => {
    const api_key = 'api_key=46077ce24d4f82dbcaa2828a33a34916'
    const base_url = `https://image.tmdb.org/t/p/w500`
    const [trendingMovies, setTrendingMovies] = useState([])

    const getTitle = (data) => {
        if (data.title !== undefined) {
            return data.title
        } else if (data.original_title !== undefined) {
            return data.original_title
        } else if (data.original_name !== undefined) {
            return data.original_name
        }
    }

    useEffect(() => {
        axios.get(`https://api.themoviedb.org/3/trending/all/week?${api_key}`)
            .then(response => setTrendingMovies(response.data.results))

    }, [])

    return (
        trendingMovies.map(movie => (
            <div className='card'>
                <h2>{getTitle(movie)}</h2>
                <p className='releaseDate'>{movie.release_date}</p>
                <p className='overview'>{movie.overview}</p>
                <p className='rate'>{movie.vote_average}</p>
                <img className='cardPoster' src={base_url + movie.poster_path} alt="jacket" />
            </div>
        ))
    );
};

export default Card;
